SELECT (SOURCEELEVATION - ESTUARYELEVATION) AS ELEVATION_DIFFERENCE
FROM MONDIAL_RIVER
WHERE NAME = 'Alz';

SELECT DESERT, COUNTRY, PROVINCE, META_REPCOL
FROM MONDIAL_GEO_DESERT
WHERE DESERT = 'Gobi';

SELECT AREA
FROM MONDIAL_PROVINCE
WHERE NAME = 'Antwerpen';

SELECT C.* FROM MONDIAL_AIRPORT A
JOIN MONDIAL_CITY C ON A.CITY = C.NAME AND A.COUNTRY = C.COUNTRY AND A.PROVINCE = C.PROVINCE
WHERE A.NAME = 'Linz';

SELECT NAME, LENGTH, AREA
FROM MONDIAL_RIVER
WHERE NAME = 'Alz';

